mapper.select:
		
		
 		SELECT m.image, c.id, c.num, c.subject, c.talent, c.c_date, c.s_day, c.address, c.lat, c.lng, c.personnel, c.gender, c.content, c.closed, c.type  FROM members m inner join contents c on c.id = m.id where rownum < 20 
		
			and type = #{type}
		

		
			and talent like '%' || #{talent} || '%'

select count(*) from contents


	where type = #{type}

	

	where talent = #{talent}
